import JobOffer from "../../images/job-post-1.svg";
import JobOfferTitle1 from "../../images/jobOfferTitle1.svg";
import ColorBlock from "../../components/ColorBlock";
import React from "react";

export default [
  {
    headerTitleWithImgTitle: "CAREER OPPORTUNITIES",
    headerTitleWithImgSubtitle: "Corporate Partnership & Sponsorship Manager",
    withHeaderTitleWithImg: true,
    headerTitleWithImgURL: JobOffer,
  },
  {
    title: "Role Overview",
  },
  {
    subtitle: 'We are seeking an out-of the box thinker hungry for a creative, fast paced, entrepreneurial work' +
        'environment to lead through the role of Corporate Partnership & Sponsorship Manager who will play a ' +
        'major role in driving partnerships and revenue for our teams at Moken.'
  },
  {
    description: [
      // {
      //   title:
      //     "We are seeking an out-of the box thinker hungry for a creative, fast paced, entrepreneurial work" +
      //     " environment to lead through the role of Corporate Partnership & Sponsorship Manager who will play a " +
      //     "major role in driving partnerships and revenue for our teams at Moken.",
      //   containerStyles: {
      //     marginBottom: 25,
      //   },
      // },
      {
        title:
          "\n" +
          "A successful candidate’s responsibilities will include developing our partner base for " +
          "startup-focused collaborations, leveraging connections and networks to secure new sponsorships and " +
          "partners, working with each of Moken’s pillar teams to integrate their unique needs, and develop a" +
          " partnership/sponsorship strategy that drives cyclical growth.",
      },
    ],
  },
  {
    title: "In this role",
  },
  {
    description: [
      {
        title:
          "You’ll develop and maintain relationships with current partners to drive new revenue through their clients;",
        withDash: true

      },
      {
        title:
          "You’ll execute on strategic plans for each individual partner to drive success;\n",
        withDash: true

      },
      {
        title:
          "You’ll develop and enhance sales materials to aid in opportunity close and partner\n" +
          "education;",
        withDash: true

      },
      {
        title:
          "You'll collaborate with global team members to close deals and maximize revenue\n" +
          "opportunities;",
        withDash: true

      },
      {
        title:
          "You will serve partners as an account manager, and a sales and product specialist\n" +
          "while providing exceptional support through the entire lifespan of the opportunity;",
        withDash: true

      },
      {
        title:
          "You will be responsible for pitching, negotiating, campaign creation, account\n" +
          "mapping, opportunity execution, and wrap-up reporting/retrospective;",
        withDash: true

      },
      {
        title:
          "You will work cross-functionally with internal stakeholders to ensure success with all\n" +
          "partners whether it be marketing, sales positioning, or continued client success;",
        withDash: true

      },
      {
        title:
          "You will articulate where the opportunities of each partner sits in the sales and\n" +
          "growth cycle and how you plan to connect them together;",
        withDash: true

      },
    ],
  },
  {
    title: "What you bring to the table",
  },
  {
    description: [
      {
        title:
          "You are results and metrics oriented with a proven track record and ability to display this effectively;",
        withDash: true

      },
      {
        title:
          "You possess superior strategic thinking, negotiating and relationship building skills;",
        withDash: true

      },
      {
        title:
          "You have outstanding written and verbal communication skills, including the ability to\n" +
          "author and deliver presentations;",
        withDash: true

      },
      {
        title:
          "You boast the ability to influence others and gain acceptance of ideas and concepts;",
        withDash: true

      },
      {
        title:
          "You have superior skills in partnership forecasting and development of an action plan\n" +
          "to close opportunities;",
        withDash: true

      },
      {
        title:
          "You have exceptional negotiation, program management, organizational,\n" +
          "prioritization, and planning skills;",
        withDash: true

      },
      {
        title:
          "You have good networking skills to help you stay connected with the right people and\n" +
          "resources;",
        withDash: true

      },
      {
        title: "You’re hardworking and motivated;",
        withDash: true

      },
      {
        title:
          "You’re a natural-born connector and thrive on building community through\n" +
          "relationships and knowledge sharing;",
        withDash: true

      },
      {
        title:
          "You enjoy independent work, but also thrive in a team environment when the scenario requires it;",
        withDash: true

      },
      {
        title:
          "You’re passionate about creating an impact and strive for success in anything you do;",
        withDash: true

      },
      {
        title:
          "You have a high degree of accountability and understand the importance of leading with honesty and integrity;",
        withDash: true

      },
      {
        title:
          "You communicate effectively and voice your opinions when you truly believe in something but are always looking and listening to alternative opinions, methods, and suggestions from those around you;",
        withDash: true

      },
      {
        title:
          "You’re confident in yourself but not afraid to ask questions or for clarification when necessary;",
        withDash: true

      },
      {
        title:
          "You’re not afraid to dive into new projects and never shy away from the unknown;",
        withDash: true

      },
      {
        title:
          "You have the ability to work flexibly to successfully manage multiple projects simultaneously, handle a fast-paced work environment, new challenges, and\n" +
          "changing priorities/objectives.",
        withDash: true

      },
    ],
  },
  {
    title: "Opportunities & Added Value",
  },
  {
    description: [
      {
        title:
          "In this role, you will gain the opportunity to establish long term relationships with growing companies globally while shaping the future of Moken and startups throughout Canada.",
      },
    ],
  },
  {
    withTitleContainerWithImg: true,
    titleContainerWithImgTitle: "TO APPLY",
    titleContainerWithImgDescription: [
      {
        title:
          "Please note: This is a one of a kind, flexible entrepreneurial style opportunity with a unique compensation package aimed at performance with a limitless earning potential. We encourage all those who believe they have what it takes to be successful and are up for the challenge to apply.",
      },
      {
        title:
          "ONLY APPLICATIONS SUBMITTED THROUGH THE FOLLOWING METHOD WILL BE REVIEWED AND CONSIDERED",
      },
      {
        title:
          "To submit an application, please email careers@mokenstartups.com with the subject line “​Knock, Knock, it's me...Hi. ​” with your resume, an introductory 30-second video, cover letter, and your portfolio (if applicable).",
      },
    ],
    titleContainerWithImgImageURL: JobOfferTitle1,
  },
  {
    colorBlock: 'black',
    colorSubtitle: 'SEE OPPORTUNITIES',
    colorTitle: 'Not the right position? Check out our other opportunities.',
    colorBlockProps: {
      containerStyles: {
        marginBottom: 0,
        backgroundColor: 'red'
      }
    }

  },
  {
    withForm: true
  }
];



